<style type="text/css">
    #jsddm { width: 580px; margin: 0 auto; padding: 0; text-align: center; }
    #jsddm li {	float: left; list-style: none; font: 12px Tahoma, Arial}
    #jsddm li a { display: block; text-decoration: none; color: #EAFFED; white-space: nowrap }
    #jsddm li a:hover { color: #24313C; }
    #jsddm li ul { margin: 0; padding: 0; position: absolute; visibility: hidden; border-top: 1px solid white; background: #A9C251; }
    #jsddm li ul li { float: none; display: inline; margin: 0; padding: 0;}
    #jsddm li ul li a {	width: auto; color: #24313C; text-align: left;  padding:1px 10px 1px 10px; }
    #jsddm li ul li a:hover { background: #8EA344; }
</style>
<script type="text/javascript">
    var timeout         = 500;
    var closetimer		= 0;
    var ddmenuitem      = 0;

    function jsddm_open() {	
        jsddm_canceltimer();
    	jsddm_close();
    	ddmenuitem = $(this).find('ul').eq(0).css('visibility', 'visible');
    }

    function jsddm_close() {
        if(ddmenuitem) ddmenuitem.css('visibility', 'hidden');
    }

    function jsddm_timer() {
        closetimer = window.setTimeout(jsddm_close, timeout);
    }

    function jsddm_canceltimer() {	
        if(closetimer) {
            window.clearTimeout(closetimer);
    		closetimer = null;
    	}
    }

    $(document).ready(function() {	
        $('#jsddm > li').bind('mouseover', jsddm_open);
    	$('#jsddm > li').bind('mouseout',  jsddm_timer);
    });
    
    document.onclick = jsddm_close;
</script>
</head>

