<script language="javascript">
$(document).ready(function(){
    initLink();
});

function initLink() {
    $('#link ul').hide();
    $('#link ul:first').show();
    $('#link li a').click(function() {
        var checkElement = $(this).next();
        var statusElement = $(this).prev();
        if ((checkElement.is('ul')) && (checkElement.is(':visible'))) {
            return false;
        }
      
        if ((checkElement.is('ul')) && (!checkElement.is(':visible'))) {
            $('#link ul:visible').slideUp('normal');
            $('#link li img').attr('src', 'images/jiatou_b.jpg');
            checkElement.slideDown('normal');
            statusElement.attr('src', 'images/jiatou_a.jpg');
            return false;
        }
    });
}

function openWin(url) {
    window.open (url,"公告列表",'height=500,width=470,top=0,left=0,toolbar=no,menubar=no,scrollbars=yes,resizable=no,location=no,status=no')
}
</script>
